        Inner = 0,

        /// <summary>Selects outer expression</summary>
        /// <example><c>value > 42.{caret}</c> captures <c>value > 42</c></example>
        /// <example><c>_args = args.{caret}</c> captures whole assignment</example>
        Outer = 1
