        /// </summary>
        public string ReplaceTemplate { get; set; }

        /// <summary>
        /// Replace message to show in the light bulb.
        /// </summary>
        public string ReplaceMessage { get; set; }

        /// <summary>
        /// Apply code formatting after code replacement.
