using Railroader.ModManager.Delegates.System.IO.Directory;
using Railroader.ModManager.Delegates.System.IO.File;
using Railroader.ModManager.Extensions;
using Railroader.ModManager.Features.CodePatchers;
using Railroader.ModManager.Interfaces;
using Serilog;

namespace Railroader.ModManager.Features;

public delegate void ApplyPatchesDelegate(ModDefinition definition, params TypePatcherInfo[] pluginPatchers);

public static class CodePatcher
{
    [ExcludeFromCodeCoverage]
    public static ApplyPatchesDelegate Factory() =>
        Factory(
            Log.Logger.ForSourceContext(),
            AssemblyDefinitionWrapper.ReadAssembly,
            AssemblyDefinitionWrapper.Write,
            Directory.GetCurrentDirectory,
            Directory.EnumerateDirectories,
            File.Delete,
            File.Move
        );

    public static ApplyPatchesDelegate Factory(ILogger logger, ReadAssemblyDefinition readAssemblyDefinition, WriteAssemblyDefinition writeAssemblyDefinition, GetCurrentDirectory getCurrentDirectory, EnumerateDirectories enumerateDirectories, Delete delete, Move move) =>
        (definition, pluginPatchers) => ApplyPatches(logger, readAssemblyDefinition, writeAssemblyDefinition, getCurrentDirectory, enumerateDirectories, delete, move, definition, pluginPatchers);

    public static readonly List<TypePatcherInfo> PluginPatchers = [
        new(typeof(ITopRightButtonPlugin), TopRightButtonPluginPatcher.Factory),
        new(typeof(IHarmonyPlugin), HarmonyPluginPatcher.Factory)
    ];

    private static void ApplyPatches(ILogger logger, ReadAssemblyDefinition readAssemblyDefinition, WriteAssemblyDefinition writeAssemblyDefinition, GetCurrentDirectory getCurrentDirectory, EnumerateDirectories enumerateDirectories, Delete delete, Move move, ModDefinition definition, TypePatcherInfo[] pluginPatchers) {
        if (pluginPatchers.Length == 0) {
            return;
        }

        logger.Information("Patching mod {ModId} ...", definition.Identifier);

        var assemblyPath = Path.Combine(definition.BasePath, definition.Identifier + ".dll");
        if (!ApplyPatches(logger, readAssemblyDefinition, writeAssemblyDefinition, getCurrentDirectory, enumerateDirectories, delete, move, assemblyPath, definition.Identifier, pluginPatchers)) {
            logger.Error("Failed to apply patches to assembly {AssemblyPath} for mod {ModId}", assemblyPath, definition.Identifier);
            return;
        }

        logger.Information("Patching complete for mod {ModId}", definition.Identifier);
    }

    private static bool ApplyPatches(ILogger logger, ReadAssemblyDefinition readAssemblyDefinition, WriteAssemblyDefinition writeAssemblyDefinition, GetCurrentDirectory getCurrentDirectory, EnumerateDirectories enumerateDirectories, Delete delete, Move move, string assemblyPath, string modId, TypePatcherInfo[] pluginPatchers) {
        var tempFilePath = Path.ChangeExtension(assemblyPath, ".patched.dll");
        var success      = false;

        AssemblyDefinition? assemblyDefinition = null;
        try {
            var resolver = new DefaultAssemblyResolver();

            // game DLLs
            resolver.AddSearchDirectory(Path.Combine(getCurrentDirectory(), "Railroader_Data", "Managed"));

            // other mods DLLs
            var thisModDir = Path.GetDirectoryName(assemblyPath);
            var modDirs    = enumerateDirectories(Path.Combine(getCurrentDirectory(), "Mods")).Where(o => o != thisModDir);
            foreach (var modDir in modDirs) {
                resolver.AddSearchDirectory(modDir);
            }

            var readParameters = new ReaderParameters { AssemblyResolver = resolver };
            assemblyDefinition = readAssemblyDefinition(assemblyPath, readParameters);
            if (assemblyDefinition == null) {
                logger.Error("Failed to load definition for assembly {AssemblyPath} for mod {ModId}", assemblyPath, modId);
                return false;
            }

            var hasPatch = false;
            var hasError = false;
            foreach (var type in assemblyDefinition.MainModule.Types) {
                try {
                    var interfaces = type.Interfaces.Select(i => i.InterfaceType?.FullName).ToList();
                    var patchers = pluginPatchers.Where(pair => interfaces.Contains(pair.MarkerType.FullName))
                                                 .Select(o => o.Factory());

                    foreach (var patcher in patchers) {
                        patcher!(assemblyDefinition, type);
                        hasPatch = true;
                    }
                } catch (Exception ex) {
                    logger.Error(ex, "Failed to patch type {TypeName} for mod {ModId}", type.FullName, modId);
                    hasError = true;
                }
            }

            success = hasPatch && !hasError;

            if (success) {
                writeAssemblyDefinition(assemblyDefinition, tempFilePath);
                logger.Debug("Wrote patched assembly to temporary file {TempPath} for mod {ModId}", tempFilePath, modId);
            } else {
                logger.Information("No patches to assembly {AssemblyPath} for mod {ModId} where applied", assemblyPath, modId);
            }

            return true;
        } finally {
            assemblyDefinition?.Dispose();
            if (success) {
                delete(assemblyPath);
                move(tempFilePath, assemblyPath);
            }
        }
    }
}

using System.Collections.Concurrent;
using System.ComponentModel;
using System.Diagnostics.CodeAnalysis;
using JetBrains.Annotations;
using Railroader.ModManager.Delegates.HarmonyLib;
using Railroader.ModManager.Extensions;
using Railroader.ModManager.Interfaces;
using Serilog;

namespace Railroader.ModManager.Features.CodePatchers;

/// <summary> Patches types implementing <see cref="IHarmonyPlugin"/> to apply or remove Harmony patches when <c>OnIsEnabledChanged</c> is called. </summary>
[PublicAPI]
public sealed class HarmonyPluginPatcher
{
    [ExcludeFromCodeCoverage]
    public static TypePatcherDelegate Factory() => Factory(Log.Logger.ForSourceContext());

    [EditorBrowsable(EditorBrowsableState.Never)]
    public static TypePatcherDelegate Factory(ILogger logger) {
        var method = MethodPatcher.Factory<IHarmonyPlugin>(logger, typeof(HarmonyPluginPatcher), typeof(PluginBase<>), "OnIsEnabledChanged");
        return (assemblyDefinition, typeDefinition) => method(assemblyDefinition, typeDefinition);
    }

    private sealed record PatcherState(bool IsEnabled, IHarmony Harmony);

    private static readonly ConcurrentDictionary<IPlugin, PatcherState> _States = new();

    /// <summary> Handles the <c>OnIsEnabledChanged</c> event for the plugin, performing patcher-specific logic when the plugin is enabled or disabled. </summary>
    /// <param name="plugin">The plugin instance. Must not be null.</param>
    /// <remarks>Method called from plugin.</remarks>
    [UsedImplicitly]
    public static void OnIsEnabledChanged(IHarmonyPlugin plugin) {
        var context = (ModdingContext)plugin.ModdingContext;

        var state = _States.GetOrAdd(plugin, _ => new PatcherState(!plugin.IsEnabled, context.HarmonyFactory(plugin.Mod.Definition.Identifier)))!;
        if (state.IsEnabled == plugin.IsEnabled) {
            return;
        }

        _States[plugin] = state with { IsEnabled = plugin.IsEnabled };

        if (plugin.IsEnabled) {
            context.Logger.Information("Applying Harmony patch for mod {ModId}", plugin.Mod.Definition.Identifier);
            state.Harmony.PatchAll(plugin.GetType().Assembly);
        } else {
            context.Logger.Information("Removing Harmony patch for mod {ModId}", plugin.Mod.Definition.Identifier);
            state.Harmony.UnpatchAll(plugin.Mod.Definition.Identifier);
        }
    }
}

using System;
using System.Collections.Generic;
using System.ComponentModel;
using System.Diagnostics.CodeAnalysis;
using System.Linq;
using System.Reflection;
using JetBrains.Annotations;
using Mono.Cecil;
using Mono.Cecil.Cil;
using Railroader.ModManager.Exceptions;
using Railroader.ModManager.Extensions;
using Serilog;
using MethodAttributes = Mono.Cecil.MethodAttributes;

namespace Railroader.ModManager.Features.CodePatchers;

public delegate bool MethodPatcherDelegate(AssemblyDefinition assemblyDefinition, TypeDefinition typeDefinition);

[PublicAPI]
public static class MethodPatcher
{
    private sealed record MethodPatcherContext(ILogger Logger, Type MarkerType, Type TargetBaseType, string TargetMethod, MethodInfo InjectedMethod);

    [ExcludeFromCodeCoverage]
    public static MethodPatcherDelegate Factory<TMarker>(Type patcherType, Type targetBaseType, string targetMethod, string? injectorMethod = null) =>
        Factory<TMarker>(Log.Logger.ForSourceContext(), patcherType, targetBaseType, targetMethod, injectorMethod);

    [EditorBrowsable(EditorBrowsableState.Never)]
    public static MethodPatcherDelegate Factory<TMarker>(ILogger logger, Type patcherType, Type targetBaseType, string targetMethod, string? injectorMethod = null) {
        var injectedMethod = patcherType.GetMethod(injectorMethod ?? targetMethod, BindingFlags.Public | BindingFlags.Static);

        List<string> errors = new();
        if (injectedMethod == null) {
            errors.Add("Injected method must be public and static.");
        } else {
            if (injectedMethod.ReturnType != typeof(void)) {
                errors.Add("Injected method must bave void return type.");
            }

            if (!injectedMethod.DeclaringType!.IsPublic) {
                errors.Add("Injected method declaring type must be public.");
            }

            var parameters = injectedMethod.GetParameters();
            if (parameters.Length != 1 || !parameters[0].ParameterType.IsAssignableFrom(typeof(TMarker))) {
                errors.Add($"Injected method must have single parameter assignable from {typeof(TMarker)}.");
            }
        }

        if (injectedMethod == null || errors.Count > 0) {
            throw new ValidationException("Failed to resolve injected method. See errors for details.", errors);
        }

        var context = new MethodPatcherContext(logger, typeof(TMarker), targetBaseType, targetMethod, injectedMethod);
        return (assemblyDefinition, typeDefinition) => context.Execute(assemblyDefinition, typeDefinition);
    }

    private static bool Execute(this MethodPatcherContext context, AssemblyDefinition assemblyDefinition, TypeDefinition typeDefinition) {
        var isBaseType = context.IsDerivedFromBaseType(typeDefinition);
        var hasMarker  = typeDefinition.Interfaces.Any(i => i.InterfaceType?.FullName == context.MarkerType.FullName);
        if (!isBaseType || !hasMarker) {
            context.Logger.Debug("Skipping patching for type {TypeName}: not derived from {BaseType} or does not implement {MarkerInterface}", typeDefinition.FullName, context.TargetBaseType, context.MarkerType);
            return false;
        }

        var module = assemblyDefinition.MainModule;
        var method = typeDefinition.Methods.FirstOrDefault(m => m.Name == context.TargetMethod) ?? context.CreateMethodOverride(typeDefinition, module);
        if (method == null) {
            return false;
        }

        // Import the patcher method
        var injectedMethodReference = module.ImportReference(context.InjectedMethod);

        // Inject patcher call (for BOTH existing AND created methods)
        var ilProcessor  = method.Body.GetILProcessor();
        var instructions = method.Body.Instructions;
        var hasPatcherCall = instructions.Any(i => i.OpCode == OpCodes.Call &&
                                                   i.Operand is MethodReference mr &&
                                                   mr.FullName == injectedMethodReference.FullName);

        if (hasPatcherCall) {
            context.Logger.Information("Skipping patch of {TypeName} as it already contain code for {PluginInterface}", typeDefinition.FullName, context.MarkerType);
            return false;
        }

        // Insert at BEGINNING (before FIRST instruction)
        var firstInstruction = instructions[0]!;
        ilProcessor.InsertBefore(firstInstruction, ilProcessor.Create(OpCodes.Ldarg_0));
        ilProcessor.InsertBefore(firstInstruction, ilProcessor.Create(OpCodes.Call, injectedMethodReference));

        context.Logger.Information("Successfully patched {TypeName} for {PluginInterface}", typeDefinition.FullName, context.MarkerType);
        return true;
    }

    private static bool IsDerivedFromBaseType(this MethodPatcherContext context, TypeDefinition typeDefinition) {
        Func<TypeReference, string?> getFullName =
            context.TargetBaseType.IsGenericTypeDefinition
                ? reference => (reference as GenericInstanceType)?.ElementType?.FullName
                : reference => reference?.FullName;

        var currentType = typeDefinition;
        while (currentType != null) {
            var baseType = currentType.BaseType;

            var fullName = getFullName(baseType);
            if (fullName == context.TargetBaseType.FullName) {
                return true;
            }

            currentType = baseType?.Resolve();
        }

        return false;
    }

    private static MethodDefinition? CreateMethodOverride(this MethodPatcherContext context, TypeDefinition typeDefinition, ModuleDefinition module) {
        context.Logger.Debug("{MethodName} method not found in {TypeName}, creating override", context.TargetMethod, typeDefinition.FullName);

        var baseMethodDef = context.FindVirtualBaseMethod(typeDefinition.BaseType);
        if (baseMethodDef == null) {
            context.Logger.Error("Virtual method '{MethodName}' not found in {TypeName} hierarchy!", context.TargetMethod, typeDefinition.FullName);
            return null;
        }

        var baseMethodRef = module.ImportReference(baseMethodDef);

        var methodAttributes = baseMethodDef.Attributes & ~(MethodAttributes.Final | MethodAttributes.NewSlot) | MethodAttributes.HideBySig;

        var method = new MethodDefinition(context.TargetMethod, methodAttributes, module.ImportReference(baseMethodDef.ReturnType));

        foreach (var param in baseMethodDef.Parameters) {
            method.Parameters.Add(new ParameterDefinition(param.Name, param.Attributes, module.ImportReference(param.ParameterType)));
        }

        var il = method.Body.GetILProcessor();
        il.Append(il.Create(OpCodes.Ldarg_0));

        for (var i = 0; i < baseMethodDef.Parameters.Count; i++) {
            var ilCode = i switch {
                0 => il.Create(OpCodes.Ldarg_1),
                1 => il.Create(OpCodes.Ldarg_2),
                2 => il.Create(OpCodes.Ldarg_3),
                _ => il.Create(OpCodes.Ldarg_S, method.Parameters[i]!)
            };
            il.Append(ilCode);
        }


        il.Append(il.Create(OpCodes.Call, baseMethodRef));
        il.Append(il.Create(OpCodes.Ret));

        typeDefinition.Methods.Add(method);
        context.Logger.Debug("Created {MethodName} override with base call in {TypeName}", context.TargetMethod, typeDefinition.FullName);
        return method;
    }

    private static MethodDefinition? FindVirtualBaseMethod(this MethodPatcherContext context, TypeReference? baseTypeRef) {
        var current = baseTypeRef?.Resolve();
        while (current != null) {
            var method = current.Methods.FirstOrDefault(m => m.Name == context.TargetMethod && m.IsVirtual);
            if (method != null) {
                return method;
            }

            current = current.BaseType?.Resolve();
        }

        return null;
    }
}

using System;
using System.Collections.Concurrent;
using System.ComponentModel;
using System.Diagnostics.CodeAnalysis;
using System.IO;
using JetBrains.Annotations;
using Railroader.ModManager.Extensions;
using Railroader.ModManager.Interfaces;
using Serilog;
using UI;
using UI.Tooltips;
using UnityEngine;
using UnityEngine.UI;
using ILogger = Serilog.ILogger;
using Object = UnityEngine.Object;

namespace Railroader.ModManager.Features.CodePatchers;

/// <summary> Patches types implementing <see cref="ITopRightButtonPlugin"/> to add UI button in the top-right area. </summary>
[PublicAPI]
[ExcludeFromCodeCoverage]
public static class TopRightButtonPluginPatcher
{
    // NOTE: This class is not tested, because code calls Unity engine methods -  if called from outside Unity they all throw this exception:
    // System.Security.SecurityException("ECall methods must be packaged into a system module.")

    [ExcludeFromCodeCoverage]
    public static TypePatcherDelegate Factory() => Factory(Log.Logger.ForSourceContext());

    [EditorBrowsable(EditorBrowsableState.Never)]
    public static TypePatcherDelegate Factory(ILogger logger) {
        var method = MethodPatcher.Factory<ITopRightButtonPlugin>(logger, typeof(TopRightButtonPluginPatcher), typeof(PluginBase<>), "OnIsEnabledChanged");
        return (assemblyDefinition, typeDefinition) => method(assemblyDefinition, typeDefinition);
    }

    private sealed record PatcherState(bool IsEnabled, GameObject? GameObject);

    private static readonly ConcurrentDictionary<IPlugin, PatcherState> _States = new();

    /// <summary> Handles the <c>OnIsEnabledChanged</c> event for the plugin, performing patcher-specific logic when the plugin is enabled or disabled. </summary>
    /// <param name="plugin">The plugin instance. Must not be null.</param>
    /// <remarks>Method called from plugin.</remarks>
    [UsedImplicitly]
    public static void OnIsEnabledChanged(ITopRightButtonPlugin plugin) {
        var topRightArea = Object.FindObjectOfType<TopRightArea>();
        if (topRightArea == null) {
            return;
        }

        var state = _States.GetOrAdd(plugin, o => new PatcherState(!o.IsEnabled, null))!;
        if (state.IsEnabled == plugin.IsEnabled) {
            return;
        }

        var logger = ((ModdingContext)plugin.ModdingContext).Logger;
        if (plugin.IsEnabled) {
            logger.Information("Applying TopRightButton patch for mod {ModId}", plugin.Mod.Definition.Identifier);

            try {
                var gameObject = AddButton(plugin, topRightArea);
                _States[plugin] = new PatcherState(true, gameObject);
            } catch (Exception exc) {
                logger.Error(exc, "Failed to add button to top right area.");
                throw;
            }
        } else {
            logger.Information("Removing TopRightButton patch for mod {ModId}", plugin.Mod.Definition.Identifier);

            if (state.GameObject != null) {
                Object.Destroy(state.GameObject);
                _States[plugin] = new PatcherState(false, null);
            }
        }
    }

    [SuppressMessage("ReSharper", "PossibleNullReferenceException")]
    [SuppressMessage("ReSharper", "AssignNullToNotNullAttribute")]
    private static GameObject AddButton(ITopRightButtonPlugin plugin, TopRightArea topRightArea) {
        var texture = LoadButtonTexture(plugin);

        var componentInChildren = topRightArea.transform.Find("Strip").gameObject.GetComponentInChildren<Button>();
        var gameObject          = Object.Instantiate(componentInChildren.gameObject, componentInChildren.transform.parent);
        gameObject.transform.SetSiblingIndex(plugin.Index);

        gameObject.GetComponent<UITooltipProvider>().TooltipInfo = new TooltipInfo(plugin.Tooltip, string.Empty);

        var button = gameObject.GetComponent<Button>();
        button.onClick = new Button.ButtonClickedEvent();
        button.onClick.AddListener(() => plugin.OnClick());

        var image = gameObject.GetComponent<Image>();
        image.sprite = Sprite.Create(texture, new Rect(0.0f, 0.0f, 128, 128), new Vector2(0.5f, 0.5f));

        return gameObject;
    }

    private static Texture2D LoadButtonTexture(ITopRightButtonPlugin plugin) {
        var pluginType = plugin.GetType();
        var path       = $"{pluginType.Namespace}.{plugin.IconName}";

        byte[] bytes;
        using (var stream = pluginType.Assembly.GetManifestResourceStream(path)) {
            using (var ms = new MemoryStream()) {
                stream!.CopyTo(ms);
                bytes = ms.ToArray();
            }
        }

        var texture = new Texture2D(128, 128, TextureFormat.DXT5, false);
        texture.LoadImage(bytes);
        return texture;
    }
}

