using Path = System.IO.Path;

namespace Railroader.ModManager.Features;

public delegate ModDefinition[] ModDefinitionLoaderDelegate();

public static class ModDefinitionLoader
{
    [ExcludeFromCodeCoverage]
    public static ModDefinitionLoaderDelegate Factory(IMemoryLogger logger) =>
        () => LoadDefinitions(logger, Directory.GetCurrentDirectory, Directory.EnumerateDirectories, File.Exists, File.ReadAllText);

    public static ModDefinition[] LoadDefinitions(IMemoryLogger logger, GetCurrentDirectory getCurrentDirectory, EnumerateDirectories enumerateDirectories, Exists exists, ReadAllText readAllText) {
        var modDefinitions = new Dictionary<string, ModDefinition>(StringComparer.OrdinalIgnoreCase);

        var baseDirectory = Path.Combine(getCurrentDirectory(), "Mods");
        foreach (var directory in enumerateDirectories(baseDirectory)) {
            var path = Path.Combine(directory, "Definition.json");
            if (!exists(path)) {
                logger.Warning("Not loading directory {directory}: Missing Definition.json.", directory);
                continue;
            }

            logger.Information("Loading definition from {directory} ...", directory);
            try {
                var jObject       = JObject.Parse(readAllText(path));
                var modDefinition = jObject.ToObject<ModDefinition>()!;

                if (modDefinitions.TryGetValue(modDefinition.Identifier, out var conflict)) {
                    logger.Error("Another mod with the same Identifier has been found in '{directory}'", conflict.BasePath);
                } else {
                    modDefinition.BasePath = directory;
                    modDefinitions.Add(modDefinition.Identifier, modDefinition);
                }
            } catch (JsonException exc) {
                logger.Error("Failed to parse definition JSON, json error: {exception}", exc);
            } catch (Exception exc) {
                logger.Error("Failed to parse definition JSON, generic error: {exception}", exc);
            }
        }

        return modDefinitions.Values.ToArray();
    }
}

using System;
using System.Collections.Generic;
using System.Diagnostics.CodeAnalysis;
using System.Linq;
using Railroader.ModManager.Extensions;
using Railroader.ModManager.Interfaces;
using Serilog;

namespace Railroader.ModManager.Features;

public delegate IReadOnlyList<ModDefinition> ModDefinitionValidatorDelegate(IReadOnlyList<ModDefinition> modDefinitions);

public static class ModDefinitionValidator
{
    [ExcludeFromCodeCoverage]
    public static ModDefinitionValidatorDelegate Factory => definitions => Execute(Log.Logger.ForSourceContext(), definitions);

    public static IReadOnlyList<ModDefinition> Execute(ILogger logger, IReadOnlyList<ModDefinition> modDefinitions) {
        List<string> errors = [];
        if (VerifyRequirementsAndConflicts(modDefinitions, errors)) {
            modDefinitions = SortByDependencies(modDefinitions, errors);
        }

        if (errors.Count > 0) {
            logger.Error("Mod preprocessing failed with error(s): {errors}", errors.ToArray());
            return [];
        }

        return modDefinitions;
    }

    private static bool VerifyRequirementsAndConflicts(IReadOnlyList<ModDefinition> modDefinitions, List<string> errors) {
        var modMap = modDefinitions.ToDictionary(
            mod => mod.Identifier,
            mod => mod,
            StringComparer.OrdinalIgnoreCase);

        foreach (var mod in modDefinitions) {
            // Verify Requirements
            if (mod.Requires != null) {
                foreach (var (requiredId, fluentVersion) in mod.Requires) {
                    if (!modMap.TryGetValue(requiredId, out var requiredMod)) {
                        errors.Add($"Mod '{mod.Identifier}' requires mod '{requiredId}', but it is not present.");
                    }

                    if (fluentVersion != null && !IsVersionSatisfied(requiredMod!.Version, fluentVersion)) {
                        errors.Add($"Mod '{mod.Identifier}' requires mod '{requiredId}' with version constraint '{fluentVersion}', but found version '{requiredMod.Version}'.");
                    }
                }
            }

            // Verify Conflicts
            if (mod.ConflictsWith != null) {
                foreach (var (conflictId, fluentVersion) in mod.ConflictsWith) {
                    if (modMap.TryGetValue(conflictId, out var conflictingMod)) {
                        if (fluentVersion == null || IsVersionSatisfied(conflictingMod.Version, fluentVersion)) {
                            errors.Add($"Mod '{mod.Identifier}' conflicts with mod '{conflictId}' (version: '{conflictingMod.Version}'{(fluentVersion != null ? $", constraint: '{fluentVersion}'" : "")}).");
                        }
                    }
                }
            }
        }

        return errors.Count == 0;
    }

    private static IReadOnlyList<ModDefinition> SortByDependencies(IReadOnlyList<ModDefinition> modDefinitions, List<string> errors) {
        var modMap = modDefinitions.ToDictionary(
            mod => mod.Identifier,
            mod => mod,
            StringComparer.OrdinalIgnoreCase);

        var sorted         = new List<ModDefinition>();
        var visited        = new HashSet<string>(StringComparer.OrdinalIgnoreCase);
        var invalidMods    = new HashSet<string>(StringComparer.OrdinalIgnoreCase);

        foreach (var mod in modDefinitions) {
            if (!visited.Contains(mod.Identifier)) {
                Visit(mod, new Stack<string>());
            }
        }

        return sorted.ToArray();

        bool Visit(ModDefinition mod, Stack<string> path) {
            if (path.Contains(mod.Identifier)) {
                path.Push(mod.Identifier);
                errors.Add($"Cyclic dependency detected: {string.Join(" -> ", path.Reverse())}");
                return false;
            }

            if (!visited.Add(mod.Identifier)) {
                return !invalidMods.Contains(mod.Identifier);
            }

            path.Push(mod.Identifier);

            var isValid = true;
            if (mod.Requires != null) {
                foreach (var requiredId in mod.Requires.Keys) {
                    if (invalidMods.Contains(requiredId)) {
                        errors.Add($"Mod '{mod.Identifier}' cannot resolve mod '{requiredId}' because mod '{requiredId}' is part of a cyclic dependency.");
                    } else if (!Visit(modMap[requiredId]!, path)) {
                        isValid = false;
                    }
                }
            }

            path.Pop();

            if (isValid) {
                sorted.Add(mod);
            } else {
                invalidMods.Add(mod.Identifier);
            }

            return isValid;
        }
    }

    private static bool IsVersionSatisfied(Version actualVersion, FluentVersion constraint) {
        return constraint.Operator switch {
            VersionOperator.Equal          => actualVersion == constraint.Version,
            VersionOperator.GreaterThan    => actualVersion > constraint.Version,
            VersionOperator.GreaterOrEqual => actualVersion >= constraint.Version,
            VersionOperator.LessThan       => actualVersion < constraint.Version,
            VersionOperator.LessOrEqual    => actualVersion <= constraint.Version,
            _                              => throw new InvalidOperationException($"Unknown version operator: {constraint.Operator}")
        };
    }
}

using System;
using System.ComponentModel;
using System.Diagnostics.CodeAnalysis;
using System.IO;
using System.Linq;
using JetBrains.Annotations;
using Newtonsoft.Json;
using Railroader.ModManager.Delegates.System.IO;
using Railroader.ModManager.Delegates.System.IO.Compression.ZipFile;
using Railroader.ModManager.Delegates.System.IO.Directory;
using Railroader.ModManager.Services;
using ZipFile = System.IO.Compression.ZipFile;

namespace Railroader.ModManager.Features;

public delegate void ExtractModsDelegate();

[PublicAPI]
public static class ModExtractor
{
    [ExcludeFromCodeCoverage]
    public static ExtractModsDelegate ExtractMods(IMemoryLogger logger) =>
        () => ExtractMods(logger, DirectoryInfoWrapper.Create, Directory.GetCurrentDirectory, ZipFileDefaults.OpenRead, ZipFile.ExtractToDirectory);

    [EditorBrowsable(EditorBrowsableState.Never)]
    public static void ExtractMods(IMemoryLogger logger, DirectoryInfoFactory directoryInfo, GetCurrentDirectory getCurrentDirectory, OpenRead openRead, ExtractToDirectory extractToDirectory) {
        var modsDirectory = Path.Combine(getCurrentDirectory(), "Mods");
        var zipFiles      = directoryInfo(modsDirectory).EnumerateFiles("*.zip").ToArray();

        foreach (var zipFile in zipFiles) {
            var zipPath = zipFile.FullName;
            // Rename zip to bak so future ExtractMods calls will no longer pick this file ... 
            zipFile.MoveTo(Path.ChangeExtension(zipFile.FullName, ".bak"));

            var success = false;
            try {
                logger.Information("Processing mod archive '{ZipPath}' for extraction.", zipPath);

                // Read Definition.json from the zip
                using var archive         = openRead(zipFile.FullName);
                var       definitionEntry = archive?.GetEntry("Definition.json");
                if (definitionEntry == null) {
                    logger.Error("Skipping archive '{ZipPath}': Missing 'Definition.json'.", zipPath);
                    return;
                }

                using var entryStream = definitionEntry.Open();
                using var reader      = new StreamReader(entryStream);
                var       json        = reader.ReadToEnd();

                ModDefinition? modDefinition;
                try {
                    modDefinition = JsonConvert.DeserializeObject<ModDefinition>(json);
                } catch (JsonException ex) {
                    logger.Error(ex, "Skipping archive '{ZipPath}': Failed to parse Definition.json.", zipPath);
                    throw;
                }

                if (modDefinition is not { IsValid: true }) {
                    logger.Error("Skipping archive '{ZipPath}': Invalid 'Definition.json'.", zipPath);
                    return;
                }

                // Define extraction path using Identifier
                var extractPath = Path.Combine(modsDirectory, modDefinition.Identifier);
                extractToDirectory(zipFile.FullName, extractPath);

                logger.Information("Successfully extracted mod '{ModId}' from '{ZipPath}' to '{ExtractPath}'.", modDefinition.Identifier, zipPath, extractPath);
                success = true;
            } catch (Exception exc) {
                logger.Error("Failed to unzip archive '{ZipPath}': {exception}.", zipPath, exc);
            } finally {
                if (!success) {
                    // Extraction failed - rename file back to zip to indicate it was not extracted
                    zipFile.MoveTo(zipPath);
                }
            }
        }
    }
}

using System.Collections.Generic;
using System.Diagnostics.CodeAnalysis;
using System.Linq;
using System.Reflection;
using Railroader.ModManager.Delegates.System.Reflection.Assembly;
using Railroader.ModManager.Extensions;
using Railroader.ModManager.Interfaces;
using Serilog;

namespace Railroader.ModManager.Features;

public delegate CreatePluginsDelegate CreatePluginsDelegateFactory(IModdingContext moddingContext);

public delegate IPlugin[] CreatePluginsDelegate(Mod mod);

public sealed class PluginManager
{
    [ExcludeFromCodeCoverage]
    public static CreatePluginsDelegate Factory(IModdingContext moddingContext) =>
        mod => CreatePlugins(moddingContext, Log.Logger.ForSourceContext(), Assembly.LoadFrom, mod);

    public static IPlugin[] CreatePlugins(IModdingContext moddingContext, ILogger logger, LoadFrom loadFrom, Mod mod) =>
        PluginFactory(moddingContext, logger, loadFrom, mod).ToArray();

    private static IEnumerable<IPlugin> PluginFactory(IModdingContext moddingContext, ILogger logger, LoadFrom loadFrom, Mod mod) {
        var assembly = loadFrom(mod.AssemblyPath!);
        if (assembly == null) {
            yield break;
        }

        foreach (var type in assembly.GetTypes()) {
            if (type.IsAbstract) {
                continue;
            }

            if (type.BaseType is not { IsGenericType: true } || type.BaseType?.GetGenericTypeDefinition() != typeof(PluginBase<>)) {
                if (typeof(IPlugin).IsAssignableFrom(type)) {
                    logger.Warning("Type {type} inherits IPluginBase but not PluginBase<> in mod {ModId}", type, mod.Definition.Identifier);
                }

                continue;
            }

            var constructor = type.GetConstructor(BindingFlags.Public | BindingFlags.NonPublic | BindingFlags.Instance, null!, [typeof(IModdingContext), typeof(IMod)], null!);
            if (constructor == null) {
                logger.Warning("Cannot find constructor that accepts IModdingContext, IMod parameters on plugin {plugin} in mod {ModId}", type, mod.Definition.Identifier);
                continue;
            }

            yield return (IPlugin)constructor.Invoke([moddingContext, mod])!;
        }
    }
}

using System;
using System.Collections;
using System.Diagnostics.CodeAnalysis;
using System.Linq;
using System.Reflection;
using HarmonyLib;
using JetBrains.Annotations;
using Logging;
using Railroader.ModManager.Features;
using Railroader.ModManager.Services;
using Serilog;
using Serilog.Events;

namespace Railroader.ModManager.HarmonyPatches;

[UsedImplicitly(ImplicitUseTargetFlags.Members)]
[HarmonyPatch]
[HarmonyPatchCategory("LogManager")]
[ExcludeFromCodeCoverage]
public static class LogManagerPatch
{
    [HarmonyPrefix]
    [HarmonyPatch(typeof(LogManager), "Awake")]
    public static void AwakePrefix(out MemoryLogger __state) {
        __state = (MemoryLogger)Log.Logger!;
    }

    [HarmonyPostfix]
    [HarmonyPatch(typeof(LogManager), "Awake")]
    public static void AwakePostfix(ref MemoryLogger __state) {
        __state.Information("Finalizing logger configuration");
        var logger = Log.Logger!;
        __state.Flush(logger);
        logger.Information("Logger configured successfully");
    }

    [HarmonyPostfix]
    [HarmonyPatch(typeof(LogManager), "MakeConfiguration")]
    public static void MakeConfigurationPostfix(ref LoggerConfiguration __result) {
        var logger = Log.Logger!;
        try {
            foreach (var pair in Bootstrapper.ModDefinitions.Where(o => o.LogLevel != null && o.LogLevel != LogEventLevel.Information)) {
                var identifier = pair.Identifier;
                if (identifier == "") {
                    identifier = "Railroader.ModManager";
                }

                logger.Information("Setting log level for {identifier} to {level}", identifier, pair.LogLevel!.Value);
                __result.MinimumLevel.Override(identifier, pair.LogLevel.Value);
            }

            RemoveUnitySinks(__result);

            // Configure modded sinks
            __result.WriteTo.Conditional(o => o.Properties.ContainsKey("SourceContext"), o => o.UnityConsole("[{Timestamp:HH:mm:ss} {Level:u3}] [{SourceContext}] {Message:lj}{NewLine}{Exception}"));
            __result.WriteTo.Conditional(o => !o.Properties.ContainsKey("SourceContext"), o => o.UnityConsole("[{Timestamp:HH:mm:ss} {Level:u3}] {Message:lj}{NewLine}{Exception}"));

        } catch (Exception exc) {
            logger.Error(exc, "Failed to configure serilog");
        }
    }

    /// <summary> Removes vanilla Unity console sinks from the configuration. </summary>
    /// <param name="configuration">The logger configuration to modify.</param>
    private static void RemoveUnitySinks(LoggerConfiguration configuration) {
        var field = typeof(LoggerConfiguration).GetField("_logEventSinks", BindingFlags.NonPublic | BindingFlags.Instance);
        if (field == null) {
            throw new InvalidOperationException($"Unable to get field {typeof(LoggerConfiguration)}::_logEventSinks");
        }

        var sinks = (IList)field.GetValue(configuration)!;

        foreach (var sink in sinks.OfType<SerilogUnityConsoleEventSink>().ToList()) {
            sinks.Remove(sink);
        }
    }
}

using System;
using System.Linq;
using Newtonsoft.Json;
using Serilog.Events;

namespace Railroader.ModManager.JsonConverters;

/// <summary> JsonConverter for <see cref="LogEventLevel"/>. </summary>
public sealed class LogEventLevelJsonConverter : JsonConverter<LogEventLevel?>
{
    /// <inheritdoc />
    public override void WriteJson(JsonWriter writer, LogEventLevel? value, JsonSerializer serializer) {
        writer.WriteValue(value.ToString());
    }

    /// <inheritdoc />
    public override LogEventLevel? ReadJson(JsonReader reader, Type objectType, LogEventLevel? existingValue, bool hasExistingValue, JsonSerializer serializer) {
        switch (reader.TokenType) {
            case JsonToken.Null:
                return null;

            case JsonToken.String: {
                var raw = (string)reader.Value!;
                if (!Enum.TryParse<LogEventLevel>(raw, true, out var parsed)) {
                    var validValues = Enum.GetValues(typeof(LogEventLevel)).Cast<LogEventLevel>().Select(o => $"'{o}'");
                    throw new JsonReaderException($"Unexpected token value '{raw}' when reading {typeof(LogEventLevel?)}. Expected: {string.Join(", ", validValues)} or null");
                }

                return parsed;
            }

            default:
                throw new JsonReaderException($"Unexpected token type {reader.TokenType} when reading {typeof(LogEventLevel?)}. Expected: {JsonToken.Null} or {JsonToken.String}.");
        }
    }
}

using System;
using System.Collections.Generic;
using System.Linq;
using Newtonsoft.Json;
using Newtonsoft.Json.Linq;
using Railroader.ModManager.Interfaces;

namespace Railroader.ModManager.JsonConverters;

/// <summary> JsonConverter for <see cref="Dictionary{String,FluentVersion}"/>. </summary>
public sealed class ModReferenceJsonConverter : JsonConverter<Dictionary<string, FluentVersion?>>
{
    /// <inheritdoc />
    public override void WriteJson(JsonWriter writer, Dictionary<string, FluentVersion?>? value, JsonSerializer serializer) {
        writer.WriteStartObject();
        foreach (var pair in value!.OrderBy(o => o.Key)) {
            writer.WritePropertyName(pair.Key);
            if (pair.Value == null) {
                writer.WriteNull();
            } else {
                writer.WriteValue(pair.Value.ToString());
            }
        }
    }

    /// <inheritdoc />
    public override Dictionary<string, FluentVersion?> ReadJson(JsonReader reader, Type objectType, Dictionary<string, FluentVersion?>? existingValue, bool hasExistingValue, JsonSerializer serializer) {
        if (reader.TokenType == JsonToken.Null) {
            return new Dictionary<string, FluentVersion?>();
        }

        var jObject = JObject.Load(reader);
        var result  = new Dictionary<string, FluentVersion?>();

        foreach (var property in jObject.Properties()) {
            var identifier = property.Name;
            if (property.Value.Type == JTokenType.Null) {
                result.Add(identifier, null);
                continue;
            }

            if (property.Value.Type != JTokenType.String) {
                throw new JsonSerializationException($"Invalid version constraint for mod '{identifier}' in {objectType.Name}. Expected a string.");
            }

            var constraint    = property.Value.ToString();
            var fluentVersion = ParseVersionConstraint(constraint, identifier);
            result.Add(identifier, fluentVersion);
        }

        return result;
    }

    private static FluentVersion? ParseVersionConstraint(string constraint, string identifier) {
        if (string.IsNullOrEmpty(constraint)) {
            return null;
        }


        var (op, skip) = constraint[0] switch {
            '>' => constraint.Length > 1 && constraint[1] == '=' ? (VersionOperator.GreaterOrEqual, 2) : (VersionOperator.GreaterThan, 1),
            '<' => constraint.Length > 1 && constraint[1] == '=' ? (VersionOperator.LessOrEqual, 2) : (VersionOperator.LessThan, 1),
            '=' => (VersionOperator.Equal, 1),
            '0' or '1' or '2' or '3' or '4' or '5' or '6' or '7' or '8' or '9'
                => (VersionOperator.GreaterOrEqual, 0),
            _ => (VersionOperator.Equal, -1)
        };

        if (skip != -1) {
            var version = VersionJsonConverter.ParseString(constraint.Substring(skip).TrimStart());
            if (version != null) {
                return new FluentVersion(version, op);
            }
        }


        throw new JsonSerializationException($"Invalid version constraint '{constraint}' for mod '{identifier}'. Expected a valid System.Version or an operator (=, >, >=, <, <=) followed by a version.");
    }
}

using System;
using Newtonsoft.Json;

namespace Railroader.ModManager.JsonConverters;

/// <summary> JsonConverter for <see cref="Version"/>. </summary>
public sealed class VersionJsonConverter : JsonConverter<Version>
{
    private const string Expected = "Expected a valid System.Version (e.g., '1', '1.2', '1.2.3' or '1.2.3.4').";

    /// <inheritdoc />
    public override void WriteJson(JsonWriter writer, Version? value, JsonSerializer serializer) {
        writer.WriteValue(value!.ToString());
    }

    /// <inheritdoc />
    public override Version ReadJson(JsonReader reader, Type objectType, Version? existingValue, bool hasExistingValue, JsonSerializer serializer) {
        if (reader.TokenType == JsonToken.String) {
            var value   = Convert.ToString(reader.Value!);

            var version = ParseString(value);
            if (version == null) {
                throw new JsonSerializationException($"Invalid version format '{value}'. {Expected}");
            }

            return version;
        }

        throw new JsonSerializationException($"Invalid version token {reader.TokenType}. {Expected}");
    }

    public static Version? ParseString(string value) {
        if (value.IndexOf('.') == -1) { // to support "version": "1" instead of "1.0"
            value += ".0";
        }

        return Version.TryParse(value, out var version) ? version : null;
    }
}

