namespace Railroader.ModManager.Tests.Extensions;

public sealed class KeyValuePairExtensionsTests
{
    [Fact]
    public void Deconstruct() {
        // Arrange
        var sut = new KeyValuePair<int, string>(42, "answer");

        // Act
        var (key, value) = sut;

        // Assert
        key.Should().Be(42);
        value.Should().Be("answer");
    }
}

using FluentAssertions;
using Newtonsoft.Json;
using Railroader.ModManager.JsonConverters;
using Serilog.Events;

namespace Railroader.ModManager.Tests.JsonConverters;

public sealed class TestsLogEventLevelJsonConverter
{
    [Theory]
    [InlineData("""{ "logLevel": "Verbose" }""", LogEventLevel.Verbose)]
    [InlineData("""{ "logLevel": "Debug" }""", LogEventLevel.Debug)]
    [InlineData("""{ "logLevel": "Information" }""", LogEventLevel.Information)]
    [InlineData("""{ "logLevel": "Warning" }""", LogEventLevel.Warning)]
    [InlineData("""{ "logLevel": "Error" }""", LogEventLevel.Error)]
    [InlineData("""{ "logLevel": "Fatal" }""", LogEventLevel.Fatal)]
    [InlineData("""{ "logLevel": "FaTaL" }""", LogEventLevel.Fatal)]
    [InlineData("""{ "logLevel": null }""", null!)]
    [InlineData("{ }", null!)]
    public void ReadValidJson(string json, LogEventLevel? expected) {
        // Act
        var actual = JsonConvert.DeserializeObject<TestData>(json);

        // Assert
        actual.Should().NotBeNull();
        actual.LogLevel.Should().Be(expected);
    }

    [Fact]
    public void ReadInvalidValue() {
        // Act
        var act = () => JsonConvert.DeserializeObject<TestData>("""{ "logLevel": "invalid" }""");

        // Assert
        act.Should().Throw<JsonReaderException>()
           .WithMessage($"Unexpected token value 'invalid' when reading {typeof(LogEventLevel?)}. Expected: 'Verbose', 'Debug', 'Information', 'Warning', 'Error', 'Fatal' or null");
    }

    [Fact]
    public void ReadInvalidType() {
        // Act
        var act = () => JsonConvert.DeserializeObject<TestData>("""{ "logLevel": 42 }""");

        // Assert
        act.Should().Throw<JsonReaderException>()
           .WithMessage($"Unexpected token type {JsonToken.Integer} when reading {typeof(LogEventLevel?)}. Expected: {JsonToken.Null} or {JsonToken.String}.");
    }

    [Fact]
    public void WriteJsonNotSupported() {
        // Act
        var actual = JsonConvert.SerializeObject(new TestData { LogLevel = LogEventLevel.Debug });

        // Assert
        actual.Should().Be("""{"logLevel":"Debug"}""");
    }

    private sealed class TestData
    {
        [JsonProperty("logLevel")]
        [JsonConverter(typeof(LogEventLevelJsonConverter))]
        public LogEventLevel? LogLevel { get; set; }
    }
}

using System;
using System.Collections.Generic;
using FluentAssertions;
using Newtonsoft.Json;
using Railroader.ModManager.Interfaces;
using Railroader.ModManager.JsonConverters;

namespace Railroader.ModManager.Tests.JsonConverters;

public sealed class TestsModReferenceJsonConverter
{
    private sealed class TestData
    {
        [JsonProperty("data")]
        [JsonConverter(typeof(ModReferenceJsonConverter))]
        public Dictionary<string, FluentVersion?> Data { get; set; } = null!;
    }

    [Theory]
    [InlineData("""{"data":{"foo":"1"}}""", VersionOperator.GreaterOrEqual, "1.0")]
    [InlineData("""{"data":{"foo":"1.0.0"}}""", VersionOperator.GreaterOrEqual, "1.0.0")]
    [InlineData("""{"data":{"foo": null }}""", null!, null!)]
    [InlineData("""{"data":{"foo": "" }}""", null!, null!)]
    [InlineData("""{"data":{"foo":"=1.0.1"}}""", VersionOperator.Equal, "1.0.1")]
    [InlineData("""{"data":{"foo":"= 1.0.1"}}""", VersionOperator.Equal, "1.0.1")]
    [InlineData("""{"data":{"foo":">1.0.0"}}""", VersionOperator.GreaterThan, "1.0.0")]
    [InlineData("""{"data":{"foo":"> 1.0.0"}}""", VersionOperator.GreaterThan, "1.0.0")]
    [InlineData("""{"data":{"foo":"<=2.0.0"}}""", VersionOperator.LessOrEqual, "2.0.0")]
    [InlineData("""{"data":{"foo":"<= 2.0.0"}}""", VersionOperator.LessOrEqual, "2.0.0")]
    [InlineData("""{"data":{"foo":"<1.2.3"}}""", VersionOperator.LessThan, "1.2.3")]
    [InlineData("""{"data":{"foo":"< 1.2.3"}}""", VersionOperator.LessThan, "1.2.3")]
    [InlineData("""{"data":{"foo":">=1.0.0"}}""", VersionOperator.GreaterOrEqual, "1.0.0")]
    [InlineData("""{"data":{"foo":">= 1.0.0"}}""", VersionOperator.GreaterOrEqual, "1.0.0")]
    public void ReadValidJson(string json, VersionOperator? @operator, string? versionString) {
        // Act
        var actual = JsonConvert.DeserializeObject<TestData>(json);

        // Assert
        actual.Should().NotBeNull();
        actual.Data.Should().NotBeNull();
        actual.Data.Count.Should().Be(1);
        var fluentVersion = actual.Data.Should().ContainKey("foo").WhoseValue;

        if (@operator == null) {
            fluentVersion.Should().BeNull();
        } else {
            fluentVersion.Should().NotBeNull();
            fluentVersion.Operator.Should().Be(@operator);
            fluentVersion.Version.Should().Be(new Version(versionString!));
        }
    }

    [Theory]
    [InlineData("""{"data":{"foo":"invalid"}}""", "invalid")]
    [InlineData("""{"data":{"foo":"~1.0.1"}}""", "~1.0.1")]
    [InlineData("""{"data":{"foo":">"}}""", ">")]
    [InlineData("""{"data":{"foo":"<"}}""", "<")]
    [InlineData("""{"data":{"foo":">="}}""", ">=")]
    [InlineData("""{"data":{"foo":">=invalid"}}""", ">=invalid")]
    public void ReadInvalidJson(string json, string value) {
        // Act
        var act = () => JsonConvert.DeserializeObject<TestData>(json);

        // Assert
        act.Should().Throw<JsonSerializationException>()
           .WithMessage($"Invalid version constraint '{value}' for mod 'foo'. Expected a valid System.Version or an operator (=, >, >=, <, <=) followed by a version.");
    }

    [Fact]
    public void ReadInvalidType() {
        // Arrange
        const string json = """{"data":{"foo":123}}""";

        // Act
        var act = () => JsonConvert.DeserializeObject<TestData>(json);

        // Assert
        act.Should().Throw<JsonSerializationException>()
           .WithMessage("Invalid version constraint for mod 'foo' in *Dictionary*. Expected a string.");
    }

    [Fact]
    public void ReadNullDictionary() {
        // Arrange
        const string json = """{"data":null}""";

        // Act
        var actual = JsonConvert.DeserializeObject<TestData>(json);

        // Assert
        actual.Should().NotBeNull();
        actual.Data.Should().NotBeNull();
        actual.Data.Should().BeEmpty();
    }

    [Fact]
    public void WriteJson() {
        // Arrange
        var testData = new TestData {
            Data = new Dictionary<string, FluentVersion?> {
                { "foo", new FluentVersion(new Version(1, 2, 3), VersionOperator.GreaterOrEqual) },
                { "bar", null }
            }
        };

        // Act
        var actual = JsonConvert.SerializeObject(testData);

        // Assert
        actual.Should().Be("""{"data":{"bar":null,"foo":">=1.2.3"}}""");
    }
}

using System;
using FluentAssertions;
using JetBrains.Annotations;
using Newtonsoft.Json;
using Railroader.ModManager.JsonConverters;

namespace Railroader.ModManager.Tests.JsonConverters;

public sealed class TestsVersionJsonConverter
{
    private const string Expected = "Expected a valid System.Version (e.g., '1', '1.2', '1.2.3' or '1.2.3.4').";

    [Theory]
    [InlineData("""{ "version": "1" }""", "1.0")]
    [InlineData("""{ "version": "1.2" }""", "1.2")]
    [InlineData("""{ "version": "1.2.3" }""", "1.2.3")]
    [InlineData("""{ "version": "1.2.3.4" }""", "1.2.3.4")]
    public void ReadValidJson(string json, string version) {
        // Act
        var actual = JsonConvert.DeserializeObject<TestData>(json);

        // Assert
        actual.Should().NotBeNull();
        actual.Version.ToString().Should().Be(version);
    }

    [Theory]
    [InlineData("""{ "version": "invalid" }""", "invalid")]
    [InlineData("""{ "version": "-1" }""", "-1")]
    public void ReadInvalidValue(string json, string value) {
        // Act
        var act = () => JsonConvert.DeserializeObject<TestData>(json);

        // Assert
        act.Should().Throw<JsonSerializationException>()
           .WithMessage($"Invalid version format '{value}'. {Expected}");
    }

    [Theory]
    [InlineData("""{ "version": {} }""", JsonToken.StartObject)]
    [InlineData("""{ "version": null }""", JsonToken.Null)]
    public void ReadInvalidType(string json, JsonToken jsonToken) {
        // Act
        var act = () => JsonConvert.DeserializeObject<TestData>(json);

        // Assert
        act.Should().Throw<JsonSerializationException>()
           .WithMessage($"Invalid version token {jsonToken}. {Expected}");
    }

    [Fact]
    public void ReadJsonWithoutVersion() {
        // Act
        var act = () => JsonConvert.DeserializeObject<TestData>("{ }");

        // Assert
        act.Should().Throw<JsonSerializationException>()
           .WithMessage("Required property 'version' not found in JSON. *");
    }

    [Fact]
    public void WriteJson() {
        // Act
        var actual = JsonConvert.SerializeObject(new TestData { Version = new Version(1,2,3) });

        // Assert
        actual.Should().Be("""{"version":"1.2.3"}""");
    }

    [UsedImplicitly]
    private sealed class TestData
    {
        [JsonProperty("version", Required = Required.Always)]
        [JsonConverter(typeof(VersionJsonConverter))]
        public Version Version { get; set; } = null!;
    }
}

