            new MemoryEntry(@"C:\Mods"),
            new MemoryEntry(@"C:\Mods\Mod1.txt", [1, 2, 3])
        ]);
        logger.DidNotReceive().Error(Arg.Any<string>(), Arg.Any<object[]>());
    }

    [Fact]
    [SuppressMessage("ReSharper", "UseObjectOrCollectionInitializer")]
    public void ExtractMods_NestedZip_SkipsRootAndExtractsCorrectly() {
        // Arrange
        var zipFile = new MemoryZip {
            { "ZipPath\\File.txt", "Content" }
        };

        var zipFile2 = new MemoryZip {
            { "Definition.json", """{"id": "MyMod", "name": "My Mod", "version": "1.0.0"}""" },
            { "File.zip", zipFile }
        };

        var memoryFs = new MemoryFs {
            { @"C:\Mods\Mod1.zip", zipFile2 }
        };

        var logger = Substitute.For<IMemoryLogger>();

        // Act
        ModExtractor.ExtractMods(logger, memoryFs.DirectoryInfo, memoryFs.Directory.GetCurrentDirectory, memoryFs.ZipFile.OpenRead, memoryFs.ZipFile.ExtractToDirectory);


        // Assert
        memoryFs.Should().BeEquivalentTo([
            new MemoryEntry(@"C:\"),
            new MemoryEntry(@"C:\Mods"),
            new MemoryEntry(@"C:\Mods\Mod1.bak", zipFile2.GetBytes()),
            new MemoryEntry(@"C:\Mods\MyMod"),
            new MemoryEntry(@"C:\Mods\MyMod\Definition.json", Encoding.UTF8.GetBytes("""{"id": "MyMod", "name": "My Mod", "version": "1.0.0"}""")),
            new MemoryEntry(@"C:\Mods\MyMod\File.zip", zipFile.GetBytes())
        ]);
        logger.DidNotReceive().Error(Arg.Any<string>(), Arg.Any<object[]>());
    }
}

using System;
using System.Linq;
using FluentAssertions;
using NSubstitute;
using Railroader.ModManager.Delegates.System.Reflection.Assembly;
using Railroader.ModManager.Features;
using Railroader.ModManager.Interfaces;
using Serilog;

namespace Railroader.ModManager.Tests.Features;

public sealed class TestsPluginManager
{
    [Fact]
    public void CreatePlugins_WhenAssemblyFailsToLoad() {
        // Arrange
        var logger         = Substitute.For<ILogger>();
        var moddingContext = Substitute.For<IModdingContext>();
        var loadFrom       = Substitute.For<LoadFrom>();
        var mod            = new Mod(logger, Substitute.For<IModDefinition>(), "assemblyPath");

        // Act
        var plugins = PluginManager.CreatePlugins(moddingContext, logger, loadFrom, mod);

        // Assert
        plugins.Should().BeEmpty();
    }

    [Fact]
    public void CreatePlugins_IgnoreAbstractClasses() {
        // Arrange
        const string source =
            """
            using Railroader.ModManager.Interfaces;

            public abstract class Foo  {};

            public abstract class Bar : PluginBase<Bar> {
                public Bar(IModdingContext moddingContext, IMod mod) 
                    : base(moddingContext, mod) {
                }
            };
            """;

        var assembly = TestUtils.BuildAssembly(source, [typeof(TestsPluginManager).Assembly.GetName().Name]);

        var logger         = Substitute.For<ILogger>();
        var moddingContext = Substitute.For<IModdingContext>();
        var loadFrom       = Substitute.For<LoadFrom>();
        loadFrom.Invoke(Arg.Any<string>()).Returns(assembly);
        var mod = new Mod(logger, Substitute.For<IModDefinition>(), "assemblyPath");

        // Act
        var plugins = PluginManager.CreatePlugins(moddingContext, logger, loadFrom, mod);

        // Assert
        plugins.Should().BeEmpty();
    }

    [Fact]
    public void CreatePlugins_IgnoreClassesNotDerivedFromPluginBase() {
        // Arrange
        const string source =
            """
            public class Foo {
            }
            """;

        var assembly = TestUtils.BuildAssembly(source, [typeof(TestsPluginManager).Assembly.GetName().Name]);

        var logger         = Substitute.For<ILogger>();
        var moddingContext = Substitute.For<IModdingContext>();
        var loadFrom       = Substitute.For<LoadFrom>();
        loadFrom.Invoke(Arg.Any<string>()).Returns(assembly);
        var mod = new Mod(logger, Substitute.For<IModDefinition>(), "assemblyPath");

        // Act
        var plugins = PluginManager.CreatePlugins(moddingContext, logger, loadFrom, mod);

        // Assert
        plugins.Should().BeEmpty();
    }

    [Fact]
    public void CreatePlugins_IgnoreClassesNotDerivedFromPluginBase_AndWarnIfIPluginBaseIsImplemented() {
        // Arrange
        const string source =
            """
            using Railroader.ModManager.Interfaces;

            public class Foo : IPlugin {
                public IModdingContext ModdingContext { get; }
                public IMod Mod { get; }
                public bool IsEnabled { get; set; }
            }
            """;

        var assembly = TestUtils.BuildAssembly(source, [typeof(TestsPluginManager).Assembly.GetName().Name]);

        var logger         = Substitute.For<ILogger>();
        var moddingContext = Substitute.For<IModdingContext>();
        var loadFrom       = Substitute.For<LoadFrom>();
        loadFrom.Invoke(Arg.Any<string>()).Returns(assembly);
        var mod = new Mod(logger, Substitute.For<IModDefinition>(), "assemblyPath");

        // Act
        var plugins = PluginManager.CreatePlugins(moddingContext, logger, loadFrom, mod);

        // Assert
        plugins.Should().BeEmpty();

        logger.Received().Warning("Type {type} inherits IPluginBase but not PluginBase<> in mod {ModId}", Arg.Is<Type>(o => o.Name == "Foo"), mod.Definition.Identifier);
    }

    [Theory]
    [InlineData("""
                using Railroader.ModManager.Interfaces;
                using Serilog;

                public sealed class TestPlugin : PluginBase<TestPlugin>
                {
                    public TestPlugin() 
                        : base(null, null) {
                    }
                }
                """)]
    [InlineData("""
                using Railroader.ModManager.Interfaces;
                using Serilog;

                public sealed class TestPlugin : PluginBase<TestPlugin>
                {
                    public TestPlugin(IModdingContext moddingContext, IMod mod, int extra) 
                        : base(moddingContext, mod) {
                    }
                }
                """)]
    public void CreatePlugins_IgnorePluginsWithInvalidConstructor(string source) {
        // Arrange
        var assembly = TestUtils.BuildAssembly(source, [typeof(TestsPluginManager).Assembly.GetName().Name]);

        var logger         = Substitute.For<ILogger>();
        var moddingContext = Substitute.For<IModdingContext>();
        var loadFrom       = Substitute.For<LoadFrom>();
        loadFrom.Invoke(Arg.Any<string>()).Returns(assembly);
        var mod = new Mod(logger, Substitute.For<IModDefinition>(), "assemblyPath");

        // Act
        var plugins = PluginManager.CreatePlugins(moddingContext, logger, loadFrom, mod);

        // Assert
        plugins.Should().BeEmpty();

        logger.Received().Warning("Cannot find constructor that accepts IModdingContext, IMod parameters on plugin {plugin} in mod {ModId}", Arg.Is<Type>(o => o.Name == "TestPlugin"), mod.Definition.Identifier);
    }

    [Fact]
    public void CreatePlugins_ReturnValidInstances() {
        // Arrange
        const string source =
            """
            using Railroader.ModManager.Interfaces;
            using Serilog;

            namespace Foo.Bar
            {
                public sealed class FirstPlugin : PluginBase<FirstPlugin>
                {
                    public FirstPlugin(IModdingContext moddingContext, IMod mod) 
                        : base(moddingContext, mod) {
                    }
                }
                
                public sealed class SecondPlugin : PluginBase<SecondPlugin>
                {
                    public SecondPlugin(IModdingContext moddingContext, IMod mod) 
                        : base(moddingContext, mod) {
                    }
                }
            }
            """;

        var assembly = TestUtils.BuildAssembly(source, [typeof(TestsPluginManager).Assembly.GetName().Name]);

        var logger         = Substitute.For<ILogger>();
        var moddingContext = Substitute.For<IModdingContext>();
        var loadFrom       = Substitute.For<LoadFrom>();
        loadFrom.Invoke(Arg.Any<string>()).Returns(assembly);
        var mod = new Mod(logger, Substitute.For<IModDefinition>(), "assemblyPath");

        // Act
        var plugins = PluginManager.CreatePlugins(moddingContext, logger, loadFrom, mod);

        // Assert
        plugins.Select(o => o.GetType().FullName).Should().BeEquivalentTo("Foo.Bar.FirstPlugin", "Foo.Bar.SecondPlugin");
    }
}

using FluentAssertions;
using Newtonsoft.Json;
using Railroader.ModManager.JsonConverters;
using Serilog.Events;

namespace Railroader.ModManager.Tests.JsonConverters;

public sealed class TestsLogEventLevelJsonConverter
{
    [Theory]
    [InlineData("""{ "logLevel": "Verbose" }""", LogEventLevel.Verbose)]
    [InlineData("""{ "logLevel": "Debug" }""", LogEventLevel.Debug)]
    [InlineData("""{ "logLevel": "Information" }""", LogEventLevel.Information)]
    [InlineData("""{ "logLevel": "Warning" }""", LogEventLevel.Warning)]
    [InlineData("""{ "logLevel": "Error" }""", LogEventLevel.Error)]
    [InlineData("""{ "logLevel": "Fatal" }""", LogEventLevel.Fatal)]
    [InlineData("""{ "logLevel": "FaTaL" }""", LogEventLevel.Fatal)]
    [InlineData("""{ "logLevel": null }""", null!)]
    [InlineData("{ }", null!)]
    public void ReadValidJson(string json, LogEventLevel? expected) {
        // Act
        var actual = JsonConvert.DeserializeObject<TestData>(json);

        // Assert
        actual.Should().NotBeNull();
        actual.LogLevel.Should().Be(expected);
    }

    [Fact]
    public void ReadInvalidValue() {
        // Act
