            new MemoryEntry(@"C:\Real\Path\Dest\Path\In"),
            new MemoryEntry(@"C:\Real\Path\Dest\Path\In\Zip"),
            new MemoryEntry(@"C:\Real\Path\Dest\Path\In\Zip\File.txt", [1, 2, 3]),
